mapper.select:
select distinct m.menu_id, m.parent_id, m.menu_name, m.url, m.visible, ifnull(m.perms,'') as perms, m.target, m.menu_type, m.icon, m.order_num, m.create_time from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id left join sys_user_role ur on rm.role_id = ur.role_id LEFT JOIN sys_role ro on ur.role_id = ro.role_id
AND m.client_id = #{clientId}
order by m.parent_id, m.order_num




		AND menu_name like concat('%', #{menuName}, '%')


		AND visible = #{visible}


order by parent_id, order_num

select distinct m.menu_id, m.parent_id, m.menu_name, m.url, m.visible, ifnull(m.perms,'') as perms, m.target, m.menu_type, m.icon, m.order_num, m.create_time
from sys_menu m
left join sys_role_menu rm on m.menu_id = rm.menu_id
left join sys_user_role ur on rm.role_id = ur.role_id
LEFT JOIN sys_role ro on ur.role_id = ro.role_id


		AND m.menu_name like concat('%', #{menu.menuName}, '%')


		AND m.visible = #{menu.visible}


		and m.client_id = #{clientId}


		and m.create_by = #{menu.createBy}


order by m.parent_id, m.order_num

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, url = #{url}, target = #{target}, menu_type = #{menuType}, visible = #{visible}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where menu_id = #{menuId}
